import React, { useEffect, useRef } from "react";
import { Link, useParams } from "react-router-dom";
import { getContact, updatePhoto } from "../api/ContactService";

const ContactDetail = ({ updateContact, updateImage }) => {
  const inpuRef = useRef();
  const [contact, setContact] = React.useState({
    name: "",
    email: "",
    phone: "",
    address: "",
    title: "",
    status: "",
    photoUrl: "",
  });

  const { id } = useParams();
  console.log(id)


  const fetchContact = async (id) => {
    try {
      const { data } = await getContact(id);
      setContact(data);
      console.log(data)
    } catch (error) {
      console.error(error);
    }
  };

  const selectImage = () => {

  };

  const updatePhoto = async (file) => {
    try {
        const formData = new FormData();
        formData.append("file", file, file.name);
        formData.append("id", data.id);
        const { data } = await updatePhoto(formData);
        setContact(data);
      } catch (error) {
        console.error(error);
      }
  };

  useEffect(() => {
    fetchContact(id);
  }, []);

  return (
    <>
        <Link to={'/'} className="link"><i className='bi bi-arrow-left'></i> Back to list</Link>
        <div className="profile">
            <div className="profile__details">
                <img src={contact.photoUrl} alt={`Profile photo of ${contact.name}`} />
                <div className="profile__metadata">
                    <p className="profile__name">{ contact.name }</p>
                    <p className="profile__muted">JPG, GIF, or PNG. Max size of 10MG</p>
                    <button onClick={selectImage()} className="btn"><i className="bi bi-cloud-upload"></i>Change Photo</button>
                </div>
            </div>
            <div className="profile__settings">Settings will go here</div>
        </div>

        <form style={{display: 'none'}}>
            <input type="file" ref={inpuRef} onChange={(event) => updatePhoto(event.target.files[0])} name="file" accept="image/*"/>
        </form>
    </>
  );
};

export default ContactDetail;
